CREATE TABLE "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "role" varchar not null, "profile_picture" varchar, "about_us" text, "business_type" varchar, "phone" varchar, "address" varchar, "google_id" varchar);
CREATE TABLE "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "image" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "product_images" ("id" integer primary key autoincrement not null, "product_id" integer not null, "image_path" varchar not null, "created_at" datetime, "updated_at" datetime, "is_thumbnail" tinyint(1) not null default '0', foreign key("product_id") references "products"("id") on delete cascade);
CREATE TABLE "messages" ("id" integer primary key autoincrement not null, "sender_id" integer not null, "receiver_id" integer not null, "content" text not null, "created_at" datetime, "updated_at" datetime, foreign key("sender_id") references "users"("id") on delete cascade, foreign key("receiver_id") references "users"("id") on delete cascade);
CREATE TABLE "product_reviews" ("id" integer primary key autoincrement not null, "product_id" integer not null, "user_id" integer not null, "rating" integer not null, "review" text not null, "created_at" datetime, "updated_at" datetime, foreign key("product_id") references "products"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE "products" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "price" numeric not null, "category_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, "thumbnail_image_id" integer, "is_thumbnail" integer, "thumbnail_image_path" varchar, foreign key("thumbnail_image_id") references product_images("id") on delete set null on update no action, foreign key("category_id") references categories("id") on delete cascade on update no action, foreign key("user_id") references users("id") on delete cascade on update no action, foreign key("is_thumbnail") references "product_images"("id") on delete set null);
CREATE TABLE "telescope_entries" ("sequence" integer primary key autoincrement not null, "uuid" varchar not null, "batch_id" varchar not null, "family_hash" varchar, "should_display_on_index" tinyint(1) not null default '1', "type" varchar not null, "content" text not null, "created_at" datetime);
CREATE TABLE "telescope_entries_tags" ("entry_uuid" varchar not null, "tag" varchar not null, foreign key("entry_uuid") references "telescope_entries"("uuid") on delete cascade, primary key ("entry_uuid", "tag"));
CREATE TABLE "telescope_monitoring" ("tag" varchar not null, primary key ("tag"));
